function openswt(){
	var myDate = new Date();
	var _hours = myDate.getHours();
	var _time  = '';
	var _host  = window.location.host.replace(/\./g,"_");
	if(_hours > 1 && _hours < 8){_time = 'night';}
	
	if(_time == 'night'){
		window.open('http://dct.zoosnet.net/LR/Chatpre.aspx?id=DCT50626314&lng=cn','new');
	}else{
		var e = arguments.length ? _host+'_' + arguments[0] : _host;
		
		if(typeof openZoosUrl == 'undefined'){
			var url = 'http://dct.zoosnet.net/LR/Chatpre.aspx?id=DCT50626314&lng=cn&r='+ encodeURIComponent(document.referrer);
			url = url + '&e=' + e +'&p=' + encodeURIComponent(location.href);
			try{
				window.open(url, 'news' + (new Date()).getTime());
			}catch(e){
				location.href = url;
			}
		}else{
			openZoosUrl('chatwin', '&e=' + e);
		}
	}
}





